sys_variable_value.value:
(function execute(inputs, outputs) {
var obj = JSON.parse(inputs['ResponseBody']);
  var resources = obj.value.map(function(item){

    return {
      label :item.name,
      name :item.name
    };
  });
  outputs.resources = {
    data:resources
  };
})(inputs, outputs);

