sys_variable_value.value:
(function execute(inputs, outputs) {
	var obj = JSON.parse(inputs['ResponseBody']);

	if (inputs.Status == 200) {
		outputs.status = "Success";
		outputs.database_object = obj;
	} else {
		outputs.status = "Error";
		outputs = new AzureDatabaseSQLErrorHandling().getErrorMessage(inputs, outputs);
	}
})(inputs, outputs);

