sys_variable_value.value:
(function execute(inputs, outputs) {
	if (inputs.Status == 200){
		outputs.status = "Success";
        outputs.errormessage = "Database Name renamed succesfully";
    }
	else {
		outputs.status = "Error";
		outputs = new AzureDatabaseSQLErrorHandling().getErrorMessageRename(inputs, outputs);

	}
})(inputs, outputs);

