sys_variable_value.value:
(function execute(inputs, outputs) {
	var responseBody = new global.JSON().decode(inputs['responseBody']);
	if (inputs.Status == 200 || inputs.Status == 202)
		outputs.status = "Success";
	else {
		outputs.status = "Error";
		outputs = new AzureDatabaseSQLErrorHandling().deleteDatabase(inputs, outputs);

	}
})(inputs, outputs);

