sys_variable_value.value:
(function execute(inputs, outputs) {
	var obj = JSON.parse(inputs['ResponseBody']);
  var datID = obj.Azure-Async Database;
  var opID = datID.split('/');
  outputs.operationalstatus = opID[12];

	if (inputs.Status == 200) {
		outputs.status = "Success";
		outputs.errormessage = "Database Updated Successfully";
	} else if (inputs.Status == 201) {
		outputs.status = "Success";
		outputs.errormessage = "Database Created Successfully"
	} else if (inputs.Status == 202) {
		outputs.status = "Success";
		outputs.errormessage = "Creating or updating the database is in progress"
	} else {
		outputs.status = "Error";
		outputs = new AzureDatabaseSQLErrorHandling().getErrorMessageCreate(inputs, outputs);
	}
})(inputs, outputs);

